mapper.select:
select

from t_history_position
where id = #{id,jdbcType=VARCHAR}
and uptime = #{uptime,jdbcType=TIMESTAMP}

mapper.delete:
delete from t_history_position
where id = #{id,jdbcType=VARCHAR}
  and uptime = #{uptime,jdbcType=TIMESTAMP}

mapper.update:
update t_history_position set terminal_id = #{terminalId,jdbcType=VARCHAR},
carno = #{carno,jdbcType=VARCHAR}, lon = #{lon,jdbcType=DECIMAL}, lat = #{lat,jdbcType=DECIMAL}, speed = #{speed,jdbcType=DECIMAL}, direction = #{direction,jdbcType=INTEGER}, height = #{height,jdbcType=DECIMAL}, mile = #{mile,jdbcType=DECIMAL}, oil = #{oil,jdbcType=DECIMAL}, `status` = #{status,jdbcType=VARCHAR}, alarmflag = #{alarmflag,jdbcType=VARCHAR}, acc = #{acc,jdbcType=TINYINT}, `position` = #{position,jdbcType=TINYINT}, lon_gcj02 = #{lonGcj02,jdbcType=DECIMAL}, lat_gcj02 = #{latGcj02,jdbcType=DECIMAL}, savetime = #{savetime,jdbcType=TIMESTAMP}, airtight = #{airtight,jdbcType=TINYINT}, load = #{load,jdbcType=TINYINT}, lift = #{lift,jdbcType=TINYINT}
where id = #{id,jdbcType=VARCHAR} and uptime = #{uptime,jdbcType=TIMESTAMP}

mapper.select:
SELECT a.terminal_id, a.uptime, c.car_no
FROM
(SELECT t.terminal_id, MAX(t.uptime) uptime
FROM ${tableName} t
WHERE t.lat >= #{startLat,jdbcType=DECIMAL} AND t.lat <= #{endLat,jdbcType=DECIMAL}
  AND t.lon >= #{startLong,jdbcType=DECIMAL} AND t.lon <= #{endLong,jdbcType=DECIMAL}
AND t.uptime >= #{startTime,jdbcType=TIMESTAMP} AND t.uptime <= #{endTime,jdbcType=TIMESTAMP}
GROUP BY t.terminal_id
) a
LEFT JOIN car_info c
ON a.terminal_id = c.terminal_id
Order By uptime DESC
